import React from "react";
import iconAuthOk from "../images/svg/UnionOk.svg";
import iconAuthFail from "../images/svg/UnionFail.svg";

function InfoTooltip({ name, isSignIn, isOpen, onClose }) {

  const icon = isSignIn ? iconAuthOk : iconAuthFail;
  const message = isSignIn
    ? "Вы успешно зарегистрировались!"
    : "Что-то пошло не так! Попробуйте ещё раз.";
console.log(message);
  return (
    <div className={`popup popup_type_${name} ${isOpen ? 'popup_opened' : ""}`}>
      <div className="popup__container popup__container_tooltip">
        <button className="popup__close" onClick={onClose}></button>
        <img
          className="popup__tooltip-img"
          src={icon}
          alt={`Иконка успешной (или не очень) авторизации`}
        />
        <h3 className="popup__title">{message}</h3>
      </div>
    </div>
  );
}

export default InfoTooltip;
