import React, { useState } from "react";
import { Link, Navigate } from "react-router-dom";

function Register({ isLoggedIn, onRegister }) {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  function handleEmailChange(evt) {
    setEmail(evt.target.value);
  }

  function handlePasswordChange(evt) {
    setPassword(evt.target.value);
  }

  function handleSubmit(e) {
    e.preventDefault();
    onRegister(email, password);
  }

  if (isLoggedIn) {
    return <Navigate to="/" />;
  }

  return (
    <form
      onSubmit={handleSubmit}
      className="authen__form"
      noValidate
      name="register">
      <h2 className="authen__title">Регистрация</h2>
      <input
        id="email"
        name="email"
        type="email"
        placeholder="Email"
        value={email}
        className="authen__input"
        onChange={handleEmailChange}
        required
      />

      <input
        id="password"
        name="password"
        type="password"
        placeholder="Пароль"
        value={password}
        className="authen__input"
        onChange={handlePasswordChange}
        minLength={8}
        maxLength={50}
        required
      />
      <button type="submit" className="authen__btn">
        Зарегистрироваться
      </button>
      <div className="authen__signin">
        <Link to="/sign-in" className="authen__login-link">
          Уже зарегистрированы? Войти
        </Link>
      </div>
    </form>
  );
}
export default Register;
