import React from "react";
import headerLogo from "../images/svg/logo.svg";
import { Routes, Route, Link, useLocation, Navigate } from "react-router-dom";

function Header(props) {
  const { loggedIn, email, logOut } = props;
  const location = useLocation();
  const linkText = location.pathname === "/sign-in" ? "Регистрация" : "Войти";
  const buttonText = loggedIn ? "Выйти" : linkText;

  return (
    <header className="header">
      <img
        className="header__logo"
        alt="Логотип сайта Место"
        src={headerLogo}
      />
      <Routes>
        <Route path="/sign-in">
          <Link to="sign-up" className="header__link">
            Регистрация
          </Link>
        </Route>

        <Route path="/sign-up">
          <Link to="sign-in" className="header__link">
            Войти
          </Link>
        </Route>

        <Route exact path="/">
          <div className="header__container">
            <p className="header__email">{headerEmail}</p>
            <Link to="sign-in" className="header__exit" onClick={onSignOut}>
              Выйти
            </Link>
          </div>
        </Route>
      </Routes>
    </header>
  );
}

export default Header;
